import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Col, Button, InputGroup, FormControl, Card, Row, ButtonGroup, Table } from 'react-bootstrap';


// set the stitchApp variable to the instapoetbot-fyeob Application
const stitchApp = Stitch.initializeDefaultAppClient('instapoetbot-fyeob');
// set the db variable to InstaPoetBot database using the 'mongodb-atlas' stitch service name
const db = stitchApp.getServiceClient(RemoteMongoClient.factory, 'mongodb-atlas').db('InstaPoetBot');
// set the poemsCollection variable to the collection of poems inside our db
const poemsCollection = db.collection('Poems');
// logs the user in with an anonymous credential
stitchApp.auth.loginWithCredential(new AnonymousCredential());

// Code to delete all Poems
// poemsCollection.deleteMany({owner_id: stitchApp.auth.user.id})
//     .then(result => console.log(`Deleted ${result.deletedCount} item(s).`))
//     .catch(err => console.error(`Delete failed with error: ${err}`));

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      prompt: "",
      poem: "",
      poemsCollection: []
    }
  }

  componentDidMount = () => {
    poemsCollection.find({},{limit: 10}).asArray()
      .then((poems) => this.setState({poemsCollection: poems}))
  }

  generatePrompt = () => {
    //Dynamically generate a random prompt
    this.setState(state => ({ prompt: "generated prompt" }))
  }

  generatePoem = (prompt) => {
    //Take a prompt and apply model and update state of Poem
    prompt ? this.setState({ poem:"generated poem" }) : alert("Please Enter a Valid Prompt")
  }

  ratePoem = (rating) => {
    //Take a rating of a poem and make a JSON object and send to MongoDB
    poemsCollection.insertOne({owner_id: stitchApp.auth.user.id, "poem": this.state.poem, "score": rating})
      .then(result => console.log(`Successfully inserted item with _id: ${result.insertedId}`))
      .then(() => this.setState({prompt: "", poem: ""}))
      .then(() => console.log(poemsCollection.find({},{limit: 10}).asArray()))
      .catch(err => console.error(`Failed to insert item: ${err}`))
      .then(() => {poemsCollection.find({},{limit: 10}).asArray()
        .then((poems) => this.setState(state => ({poemsCollection: poems})))})
  }

  handleChange = (e) => {
    const target = e.target
    const value = target.value
    const name = target.name
    this.setState({[name]:value})
  }

  render (){
    const { prompt, poem, poemsCollection } = this.state
    const containerStyle  = { display:"flex", justifyContent:"center", marginTop:"4rem" }
    const columnStyle  = { display:"flex", flexDirection:"column", justifyContent:"center",}
    const eStyle = { marginBottom:"2rem" }
    const poetryTable = poemsCollection.map((v,i) =>
      <tr key={v._id.id}>
        <td key={v.poem.length + i} >{v.poem}</td>
        <td key={v.score + i} >{v.score}</td>
      </tr>
    )
    return (
      <Container style={containerStyle}>
          <Col lg={8} style={columnStyle}>
              <h1 style={eStyle}>InstaPoetBot</h1>
                <InputGroup style={eStyle}>
                  <FormControl as="textarea" aria-label="With textarea" placeholder="Please enter a prompt word or phrase of more than 5 characters or click generate prompt for a random prompt to be provided." name="prompt" onChange={(e) => this.handleChange(e)} value={prompt}/>
                </InputGroup>
                {prompt.length < 6 && <Button variant='primary' onClick={() => this.generatePrompt()} style={eStyle}>Generate Prompt</Button>}
                {prompt.length > 5 && <Button variant='primary' onClick={() => this.generatePoem(prompt)} style={eStyle}>Generate Poem</Button>}
                {poem &&
                <div className="d-flex flex-column" style={eStyle}>
                  <Card>
                    <Card.Body>{poem}</Card.Body>
                  </Card>
                </div>}
                {poem &&
                <div className="d-flex flex-column" style={eStyle}>
                    <ButtonGroup>
                    <Button variant='danger' onClick={() => this.ratePoem(0)}>Terrible!</Button>
                    <Button variant='warning'onClick={() => this.ratePoem(1)}>Fail</Button>
                    <Button variant='primary'onClick={() => this.ratePoem(2)}>Pass</Button>
                    <Button variant='success'onClick={() => this.ratePoem(3)}>Great!</Button>
                    </ButtonGroup>
                </div>}
                {poemsCollection &&
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>Poem</th>
                    <th>Score</th>
                  </tr>
                </thead>
                <tbody>
                  {poetryTable}
                </tbody>
              </Table>}
            </Col>
      </Container>
    );
  }
}

export default App;
