import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Col, Button, InputGroup, FormControl, Card, Row, ButtonGroup, Table } from 'react-bootstrap';


// set the stitchApp variable to the instapoetbot-fyeob Application

// Code to delete all outputs
// outputsCollection.deleteMany({owner_id: stitchApp.auth.user.id})
//     .then(result => console.log(`Deleted ${result.deletedCount} item(s).`))
//     .catch(err => console.error(`Delete failed with error: ${err}`));

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      input: " ",
      output: [],
    }
  }


  generateInfo = () => {
    //Dynamically generate a random input
    this.setState(state => ({ info: "generated input" }))
  }

  generateoutput = (input) => {
    //Take a input and apply model and update state of output
    input ? this.setState({ output:"Search" }) : alert("Please Enter a Valid input")
  }

  handleChange = (e) => {
    const target = e.target
    const value = target.value
    const name = target.name
    this.setState({[name]:value})
  }

  render (){
    const { input, output } = this.state
    const containerStyle  = { display:"flex", justifyContent:"center", marginTop:"4rem" }
    const columnStyle  = { display:"flex", flexDirection:"column", justifyContent:"center",}
    const eStyle = { marginBottom:"2rem", justifyContent:"center" }

    return (
      <Container style={containerStyle}>
          <Col lg={8} style={columnStyle}>
              <h1 style={eStyle}>Royalty Free Music Finder</h1>
                <InputGroup style={eStyle}>
                  <FormControl as="textarea" aria-label="With textarea" placeholder="Enter an Emotion, feeling or mood to search for royalty free music." name="input" onChange={(e) => this.handleChange(e)} value={input}/>
                </InputGroup>
                {input.length < 6 && <Button variant='primary' onClick={() => this.generateinput()} style={eStyle}>Search</Button>}
                {input.length > 5 && <Button variant='primary' onClick={() => this.generateoutput(input)} style={eStyle}>Generate output</Button>}
                {output &&
                <div className="d-flex flex-column" style={eStyle}>
                  <Card>
                    <Card.Body>{output}</Card.Body>
                  </Card>
                </div>}


              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>output</th>
                    <th>Rating</th>
                  </tr>
                </thead>
                <tbody>

                </tbody>
              </Table>}
            </Col>
      </Container>
    );
  }
}

export default App;
